// Comprehensive lead scoring and qualification system
const leadData = items[0].json;

// Base lead score calculation
let leadScore = 30; // Base score

// Analysis type scoring
if (leadData.analysisType === 'photo') {
  leadScore += 25; // Photo analysis shows higher engagement
} else {
  leadScore += 10; // Manual analysis
}

// Room type scoring (business value)
const roomTypeScores = {
  'living_room': 20, // High-value room
  'bedroom': 18,     // High-value room
  'dining_room': 15, // Medium-value room
  'study': 12,       // Medium-value room
  'child_bedroom': 10, // Lower-value but emotional connection
  'bathroom': 8,     // Lower-value room
  'playroom': 6      // Lowest-value room
};
leadScore += roomTypeScores[leadData.roomType] || 5;

// Age group scoring (purchasing power indicator)
const ageGroupScores = {
  'adult': 20,    // Primary target
  '13-18': 8,     // Limited purchasing power
  '7-12': 5,      // Parents might be buyers
  '4-6': 5,       // Parents might be buyers
  '0-3': 3        // Parents focused on safety over aesthetics
};
leadScore += ageGroupScores[leadData.ageGroup] || 10;

// Engagement indicators
if (leadData.customerEmail && leadData.customerEmail.length > 0) {
  leadScore += 15; // Email provided = higher engagement
}
if (leadData.customerPhone && leadData.customerPhone.length > 0) {
  leadScore += 10; // Phone provided = very high engagement
}

// Analysis quality scoring
if (leadData.recommendations && leadData.recommendations.length > 0) {
  const topConfidence = leadData.recommendations[0].confidence || 0;
  if (topConfidence > 90) leadScore += 15;
  else if (topConfidence > 75) leadScore += 10;
  else if (topConfidence > 60) leadScore += 5;
}

// Time-based engagement (repeat visitor bonus)
if (leadData.previousAnalyses && leadData.previousAnalyses > 0) {
  leadScore += Math.min(leadData.previousAnalyses * 5, 20); // Max 20 bonus points
}

// Ensure score is within 0-100 range
leadScore = Math.max(0, Math.min(100, leadScore));

// Determine lead qualification tier
let qualification = 'cold';
let priority = 'low';
let followUpAction = 'basic_nurture';
let assignedTo = 'marketing_team';

if (leadScore >= 85) {
  qualification = 'hot';
  priority = 'urgent';
  followUpAction = 'immediate_consultation';
  assignedTo = 'senior_designer';
} else if (leadScore >= 70) {
  qualification = 'warm';
  priority = 'high';
  followUpAction = 'personal_outreach';
  assignedTo = 'sales_team';
} else if (leadScore >= 50) {
  qualification = 'lukewarm';
  priority = 'medium';
  followUpAction = 'targeted_followup';
  assignedTo = 'junior_designer';
} else {
  qualification = 'cold';
  priority = 'low';
  followUpAction = 'email_nurture';
  assignedTo = 'marketing_team';
}

// Generate lead insights and recommendations
const leadInsights = {
  demographicProfile: generateDemographicProfile(leadData),
  behavioralIndicators: generateBehavioralIndicators(leadData),
  purchaseReadiness: assessPurchaseReadiness(leadScore, leadData),
  recommendedActions: generateRecommendedActions(qualification, leadData),
  potentialValue: estimatePotentialValue(leadData, leadScore)
};

// Create comprehensive lead record
const crmLeadRecord = {
  // Lead identification
  leadId: generateLeadId(),
  source: 'sanzo_color_advisor',
  sourceDetails: {
    analysisType: leadData.analysisType,
    roomType: leadData.roomType,
    timestamp: new Date().toISOString()
  },

  // Customer information
  customer: {
    name: leadData.customerName || 'Prospect',
    email: leadData.customerEmail || '',
    phone: leadData.customerPhone || '',
    preferredContact: leadData.preferredContact || 'email',
    timezone: 'Europe/Istanbul'
  },

  // Lead scoring and qualification
  scoring: {
    totalScore: leadScore,
    qualification: qualification,
    priority: priority,
    scoreBreakdown: {
      analysisType: leadData.analysisType === 'photo' ? 25 : 10,
      roomType: roomTypeScores[leadData.roomType] || 5,
      ageGroup: ageGroupScores[leadData.ageGroup] || 10,
      contactInfo: (leadData.customerEmail ? 15 : 0) + (leadData.customerPhone ? 10 : 0),
      analysisQuality: leadData.recommendations?.[0]?.confidence > 90 ? 15 : (leadData.recommendations?.[0]?.confidence > 75 ? 10 : 5),
      engagementHistory: leadData.previousAnalyses ? Math.min(leadData.previousAnalyses * 5, 20) : 0
    }
  },

  // Assignment and routing
  assignment: {
    assignedTo: assignedTo,
    team: getTeamByAssignment(assignedTo),
    assignedAt: new Date().toISOString(),
    followUpAction: followUpAction,
    nextContactDate: calculateNextContactDate(priority)
  },

  // Project details
  project: {
    roomType: leadData.roomType,
    roomTypeDisplay: {
      'living_room': 'Oturma OdasÄ±',
      'bedroom': 'Yatak OdasÄ±',
      'child_bedroom': 'Ã‡ocuk OdasÄ±',
      'study': 'Ã‡alÄ±ÅŸma OdasÄ±',
      'dining_room': 'Yemek OdasÄ±',
      'bathroom': 'Banyo',
      'playroom': 'Oyun OdasÄ±'
    }[leadData.roomType] || leadData.roomType,
    ageGroup: leadData.ageGroup,
    budget: estimateBudgetRange(leadData.roomType, leadData.ageGroup),
    timeline: estimateProjectTimeline(qualification),
    complexity: assessProjectComplexity(leadData)
  },

  // Analysis data
  analysisData: {
    type: leadData.analysisType,
    recommendations: leadData.recommendations || [],
    extractedColors: leadData.extractedColors || [],
    confidence: leadData.recommendations?.[0]?.confidence || 0,
    completedAt: new Date().toISOString()
  },

  // Insights and AI recommendations
  insights: leadInsights,

  // Workflow and tracking
  workflow: {
    currentStage: 'initial_contact',
    nextActions: generateNextActions(qualification, followUpAction),
    expectedCloseDate: calculateExpectedCloseDate(qualification, leadScore),
    conversionProbability: calculateConversionProbability(leadScore, qualification)
  },

  // Timestamps
  createdAt: new Date().toISOString(),
  updatedAt: new Date().toISOString(),
  lastActivityAt: new Date().toISOString()
};

// Helper functions
function generateLeadId() {
  const timestamp = Date.now();
  const random = Math.random().toString(36).substring(2, 8);
  return `SCL-${timestamp}-${random.toUpperCase()}`;
}

function generateDemographicProfile(data) {
  return {
    ageSegment: data.ageGroup || 'adult',
    lifeStage: inferLifeStage(data.ageGroup, data.roomType),
    householdType: inferHouseholdType(data.roomType, data.ageGroup),
    designAwareness: data.analysisType === 'photo' ? 'high' : 'medium'
  };
}

function generateBehavioralIndicators(data) {
  return {
    digitalEngagement: data.analysisType === 'photo' ? 'high' : 'medium',
    decisionMakingStyle: data.customerPhone ? 'quick' : 'research-oriented',
    informationSeekingBehavior: data.previousAnalyses > 0 ? 'thorough' : 'initial',
    communicationPreference: data.customerPhone ? 'direct' : 'digital'
  };
}

function assessPurchaseReadiness(score, data) {
  if (score >= 85) return 'ready_to_buy';
  if (score >= 70) return 'actively_considering';
  if (score >= 50) return 'researching';
  return 'awareness_stage';
}

function generateRecommendedActions(qual, data) {
  const actions = {
    hot: ['immediate_phone_call', 'schedule_consultation', 'send_portfolio', 'prepare_quote'],
    warm: ['personalized_email', 'share_case_studies', 'offer_consultation', 'social_proof'],
    lukewarm: ['nurture_sequence', 'educational_content', 'social_media_retargeting'],
    cold: ['email_newsletter', 'blog_content', 'social_media_awareness']
  };
  return actions[qual] || actions.cold;
}

function estimatePotentialValue(data, score) {
  const baseValues = {
    'living_room': 8000,
    'bedroom': 6000,
    'dining_room': 5000,
    'study': 3000,
    'child_bedroom': 2500,
    'bathroom': 2000,
    'playroom': 1500
  };

  const baseValue = baseValues[data.roomType] || 2000;
  const scoreMultiplier = score / 100;

  return {
    estimated: Math.round(baseValue * scoreMultiplier),
    range: {
      min: Math.round(baseValue * scoreMultiplier * 0.6),
      max: Math.round(baseValue * scoreMultiplier * 1.4)
    },
    confidence: score >= 70 ? 'high' : (score >= 50 ? 'medium' : 'low')
  };
}

function inferLifeStage(ageGroup, roomType) {
  if (roomType === 'child_bedroom' || roomType === 'playroom') return 'family';
  if (ageGroup === 'adult') return 'established';
  if (ageGroup === '13-18') return 'young_adult';
  return 'family';
}

function inferHouseholdType(roomType, ageGroup) {
  if (roomType.includes('child') || roomType === 'playroom') return 'family_with_children';
  if (ageGroup === 'adult') return 'adult_household';
  return 'mixed_household';
}

function getTeamByAssignment(assignment) {
  const teamMapping = {
    senior_designer: 'design_team',
    sales_team: 'sales_team',
    junior_designer: 'design_team',
    marketing_team: 'marketing_team'
  };
  return teamMapping[assignment] || 'general';
}

function calculateNextContactDate(priority) {
  const now = new Date();
  const daysToAdd = {
    urgent: 1,
    high: 2,
    medium: 5,
    low: 14
  }[priority] || 7;

  const nextDate = new Date(now.getTime() + (daysToAdd * 24 * 60 * 60 * 1000));
  return nextDate.toISOString();
}

function estimateBudgetRange(roomType, ageGroup) {
  const budgetRanges = {
    'living_room': { min: 5000, max: 15000 },
    'bedroom': { min: 3000, max: 10000 },
    'dining_room': { min: 2500, max: 8000 },
    'study': { min: 1500, max: 5000 },
    'child_bedroom': { min: 1000, max: 4000 },
    'bathroom': { min: 800, max: 3000 },
    'playroom': { min: 500, max: 2000 }
  };
  return budgetRanges[roomType] || { min: 1000, max: 5000 };
}

function estimateProjectTimeline(qualification) {
  const timelines = {
    hot: '1-2 weeks',
    warm: '2-4 weeks', 
    lukewarm: '1-2 months',
    cold: '3+ months'
  };
  return timelines[qualification] || '2-4 weeks';
}

function assessProjectComplexity(data) {
  let complexity = 1; // Base complexity

  if (data.analysisType === 'photo') complexity += 0.5;
  if (data.roomType === 'living_room' || data.roomType === 'bedroom') complexity += 0.3;
  if (data.recommendations && data.recommendations.length > 2) complexity += 0.2;

  if (complexity >= 2) return 'high';
  if (complexity >= 1.5) return 'medium';
  return 'low';
}

function generateNextActions(qualification, followUpAction) {
  const nextActions = {
    immediate_consultation: [
      'Schedule consultation call within 24h',
      'Prepare design portfolio',
      'Send welcome package',
      'Assign senior designer'
    ],
    personal_outreach: [
      'Personalized follow-up email',
      'Share relevant case studies',
      'Offer free consultation',
      'Connect on social media'
    ],
    targeted_followup: [
      'Add to nurture sequence',
      'Send design tips',
      'Retarget with ads',
      'Quarterly check-in'
    ],
    email_nurture: [
      'Add to newsletter',
      'Send educational content',
      'Include in blog updates',
      'Annual survey'
    ]
  };
  return nextActions[followUpAction] || nextActions.email_nurture;
}

function calculateExpectedCloseDate(qualification, score) {
  const now = new Date();
  let daysToClose = 90; // Default

  if (qualification === 'hot' && score >= 90) daysToClose = 14;
  else if (qualification === 'hot') daysToClose = 30;
  else if (qualification === 'warm') daysToClose = 60;
  else if (qualification === 'lukewarm') daysToClose = 120;
  else daysToClose = 180;

  const closeDate = new Date(now.getTime() + (daysToClose * 24 * 60 * 60 * 1000));
  return closeDate.toISOString();
}

function calculateConversionProbability(score, qualification) {
  let probability = score / 2; // Base probability from score

  const qualificationBonus = {
    hot: 30,
    warm: 15,
    lukewarm: 5,
    cold: 0
  };

  probability += qualificationBonus[qualification] || 0;
  return Math.min(95, Math.max(5, Math.round(probability))); // Between 5-95%
}

return [{ json: crmLeadRecord }];

